"use strict";

Component({
  mixins: [],
  data: {
    $uiName: 'inpage-guide',
    visible: true
  },
  props: {
    className: '',
    type: 'normal',
    // normal, blue
    closable: true,
    title: '',
    desc: '',
    actionText: '',
    imgSrc: '',
    onActionTap: null,
    onCloseTap: null
  },
  didMount: function didMount() {},
  didUpdate: function didUpdate() {},
  didUnmount: function didUnmount() {},
  methods: {
    onItemActionTap: function onItemActionTap() {
      if (this.props.onActionTap) {
        this.props.onActionTap();
      }
    },
    onItemCloseTap: function onItemCloseTap() {
      this.setData({
        visible: false
      });
      if (this.props.onCloseTap) {
        this.props.onCloseTap();
      }
    }
  }
});