"use strict";

Component({
  mixins: [],
  data: {
    $uiName: 'textfold',
    isExpand: false
  },
  props: {
    placeholder: '显示全部',
    className: '',
    text: '',
    max: 80,
    onDmTap: function onDmTap() {},
    onTap: function onTap() {},
    onDmExpand: function onDmExpand() {},
    onExpand: function onExpand() {}
  },
  methods: {
    onDmTap: function onDmTap(e) {
      this.props.onDmTap();
      this.props.onTap();
    },
    onDmExpand: function onDmExpand(e) {
      var _this = this;
      this.setData({
        isExpand: true
      }, function () {
        _this.props.onDmExpand();
        _this.props.onExpand();
      });
    }
  }
});