"use strict";

Page({
  data: {
    tagData: [{
      date: '2020-01-01',
      tag: '颜色1',
      tagColor: 1
    }, {
      date: '2020-01-02',
      tag: '颜色2',
      tagColor: 2
    }, {
      date: '2020-01-03',
      tag: '颜色3',
      tagColor: 3
    }, {
      date: '2020-01-04',
      tag: '颜色4',
      tagColor: 4
    }, {
      date: '2020-01-05',
      tag: '颜色5',
      tagColor: 5
    }, {
      date: '2020-01-10',
      tag: '禁用',
      disable: true
    }],
    singleDateString: new Date().toLocaleDateString(),
    rangeDateString: ''
  },
  handleSelect: function handleSelect() {},
  onMonthChange: function onMonthChange() {},
  onYearChange: function onYearChange() {},
  handleSingleSelect: function handleSingleSelect(result) {
    var date = result[0];
    this.setData({
      singleDateString: date.toLocaleDateString()
    });
  },
  handleRangeSelect: function handleRangeSelect(result) {
    var startDate = result[0];
    var endDate = result[1];
    this.setData({
      rangeDateString: "".concat(startDate.toLocaleDateString(), " ~ ").concat(endDate.toLocaleDateString())
    });
  },
  onSelectHasDisableDate: function onSelectHasDisableDate() {
    my.alert({
      content: 'SelectHasDisableDate'
    });
  },
  toHome: function toHome() {
    my.navigateTo({
      url: '/src/demo/index/index'
    });
  }
});