"use strict";

Page({
  data: {
    clearValue: '我是默认文本',
    autoClearValue: '带有清空功能'
  },
  onLoad: function onLoad() {},
  onTap: function onTap(e) {
    console.log('onTap', e);
  },
  onInput: function onInput(e) {
    var _this = this;
    // 异步返回错误示例
    setTimeout(function () {
      _this.setData({
        error: 'error 222'
      });
    }, 300);
    console.log('onInput', e.detail.value);
  },
  onBlur: function onBlur(e) {
    console.log('onBlur', e.detail);
  },
  onConfirm: function onConfirm(e) {
    console.log('onConfirm', e.detail.value);
  },
  onClear: function onClear(e) {
    console.log('onClear', e.detail.value);
  },
  onCheckValidate: function onCheckValidate(value) {
    // 同步返回错误示例
    return 'error';
  },
  onFocus: function onFocus(e) {
    console.log('onFocus', e);
  }
});