"use strict";

Component({
  props: {
    show: false,
    className: '',
    iconUrl: '',
    iconClassName: '',
    title: '',
    subTitle: '',
    extraText: '',
    extraClassName: ''
  },
  data: {
    showPage: true,
    isMove: false // 判断是否下滑 控制是否展示消失动画
  },

  methods: {
    onTap: function onTap(e) {
      this.props.onTap && this.props.onTap(e);
    },
    onClose: function onClose(e) {
      this.props.onClose && this.props.onClose(e);
    },
    touchStart: function touchStart(e) {
      this.clientY = e.changedTouches[0].clientY;
    },
    touchEnd: function touchEnd(e) {
      var _this = this;
      var newClintY = e.changedTouches[0].clientY;
      if (this.clientY && newClintY - this.clientY > 50) {
        this.setData({
          isMove: true
        });
        setTimeout(function () {
          _this.setData({
            showPage: false
          });
        }, 500);
      }
    }
  }
});