"use strict";

Page({
  data: {
    isMessageShow: false,
    type: 'success',
    textMap: {
      success: 'This is a success message',
      error: 'This is a error message',
      warning: 'This is a warning message',
      overflow: 'This is a long long long long long long long long message',
      duration: 'This is a 5s message'
    },
    duration: 2000
  },
  onLoad: function onLoad() {},
  onBtnTap: function onBtnTap(e, comp) {
    var type = comp.props['data-type'];
    var textType = comp.props['data-text-type'];
    this.setData({
      type: type,
      isMessageShow: true,
      content: this.data.textMap[textType],
      duration: textType === 'duration' ? 5000 : 2000
    });
  },
  onDmMessageHide: function onDmMessageHide() {
    this.setData({
      isMessageShow: false
    });
  },
  toHome: function toHome() {
    my.navigateTo({
      url: '/src/demo/index/index'
    });
  }
});