"use strict";

Page({
  data: {
    checked: true
  },
  onLoad: function onLoad() {},
  onChange: function onChange(index, checked) {
    this.setData({
      checked: !this.data.checked
    });
    console.log('onChange %c [ isChecked ]', 'font-size:13; background:pink; color:#bf2c9f;', !this.data.checked, index, checked);
  },
  onExtraTap: function onExtraTap(index, checked) {
    console.log("on Extra Tap", index, checked);
  }
});