"use strict";

Component({
  mixins: [],
  data: {
    $uiName: 'row'
  },
  props: {
    className: '',
    gutter: 0,
    // 间距
    align: 'top',
    // 垂直对齐方式：top | middle | bottom
    justify: 'start',
    // 水平排列方式：start end center space-around space-between
    onDmTap: function onDmTap() {}
  },
  didMount: function didMount() {},
  didUpdate: function didUpdate() {},
  didUnmount: function didUnmount() {},
  methods: {
    onDmTap: function onDmTap(e) {
      this.props.onDmTap(e, this);
    }
  }
});