"use strict";

Page({
  data: {
    withSwiperTabActiveIndex: 0,
    activeIndexDefault: 0,
    activeIndex: 0,
    activeIndexScroll: 5,
    activeIndex2: 0,
    tabs: [{
      title: '选项1',
      hasBadge: true
    }, {
      title: '选项2',
      hasBadge: true
    }]
  },
  onLoad: function onLoad() {
    var _this = this;
    setTimeout(function () {
      _this.setData({
        activeIndexDefault: 1
      });
    }, 2000);
  },
  onItemTap: function onItemTap(index) {
    console.log(index);
    this.setData({
      activeIndex: index
    });
  },
  onItemScrollTap: function onItemScrollTap(index) {
    this.setData({
      activeIndexScroll: index
    });
  },
  onWithSwiperTabItemTap: function onWithSwiperTabItemTap(index) {
    console.error("index.====", index);
    this.setData({
      withSwiperTabActiveIndex: index
    });
  },
  onSwiperChange: function onSwiperChange(e) {
    this.setData({
      withSwiperTabActiveIndex: e && e.detail && e.detail.current
    });
  }
});